import { Component ,OnInit} from '@angular/core';
import { QuizMakerService } from './services/quiz-maker.service';
@Component({
  selector: 'app-quiz-maker',
  templateUrl: './quiz-maker.component.html',
  styleUrls: ['./quiz-maker.component.css']
})
export class QuizMakerComponent implements OnInit {

  categories:String[]=[];
  constructor(private quizMakerService:QuizMakerService){}

  ngOnInit(): void {

    this.getCategories();
  }
  getCategories():string[] {
    this.quizMakerService.getQuizCategories().subscribe(
        res => {
          console.log(res)
            if (res && res['result'] && res['status'] === 200) {
                this.categories = res['result'];
            }
        }
    );
}
}
