import { Component ,OnInit} from '@angular/core';
import { QuizMakerService } from '../services/quiz-maker.service';
import { Category } from '../models/category';
import { Quiz } from '../models/quiz';
import { QuizDetails } from '../models/quizDetails';
@Component({
  selector: 'app-quiz-maker',
  templateUrl: './quiz-maker.component.html',
  styleUrls: ['./quiz-maker.component.css'],
  providers: [QuizMakerService]
})
export class QuizMakerComponent implements OnInit {

  categories:Category[]=[];
  difficulties:string[]=['Easy','medium','hard'];
  quizDetails:QuizDetails;
  constructor(private quizService:QuizMakerService){}

  ngOnInit(): void {

    this.getCategories();
  }
  getCategories():void{
    this.quizService.getQuizCategories().subscribe(
        res => {
          const obj = JSON.parse(JSON.stringify(res));
            this.categories =  obj.trivia_categories;
            console.log(this.categories);
        }
    );
}
createQuiz(inputParam:Quiz){

  this.quizService.createQuiz(inputParam)
      .subscribe(data => {
       this.quizDetails=JSON.parse(JSON.stringify(data));

      })  ;

}
}
<div>
    <h2>QUIZ MAKER</h2>
    <div>
      <form #myForm="ngForm" (submit)="createQuiz(myForm.value)" novalidate>

        <select name="categorySelect" id="categorySelect" placeholder="Select a Category" ngModel>
          <option *ngFor="let cat of categories" value="{{cat.id}}">{{cat.name}}</option>

        </select>


          <select name="difficultySelect" id="difficultySelect" ngModel>
            <option *ngFor="let difficultyLevel of difficulties">{{difficultyLevel}}</option>

          </select><button type="submit" id="createBtn" >Create</button>
          </form>
    </div>

</div>
