import { Injectable } from '@angular/core';
import { HttpClient, HttpErrorResponse } from '@angular/common/http';
import { Observable, throwError } from 'rxjs';
import { Quiz } from '../models/quiz';
@Injectable({
  providedIn: 'root'
})
export class QuizMakerService {

  constructor(private httpClient: HttpClient) { }

  getQuizCategories() : Observable<string[]> {
    return this.httpClient.get<string[]>(`https://opentdb.com/api_category.php`);
}
createQuiz(inputParam:Quiz):Observable<string[]>{
  const obj=JSON.stringify(inputParam);
//  return this.httpClient.get(`${environment.baseUrl}${AccountAdjustmentDetailAPIConst.GET_YEAR.URL}`);'
  let url=`https://opentdb.com/api.php?amount=5&category=`+obj.id+`&difficulty=`+obj.difficulty+`&type=multiple`;
  return this.httpClient.get<string[]>(url);
}
}
