import { Component ,OnInit} from '@angular/core';
import { QuizMakerService } from '../services/quiz-maker.service';
import { Category } from '../models/category';
import { Quiz } from '../models/quiz';
import { QuizDetails } from '../models/quizDetails';
@Component({
  selector: 'app-quiz-maker',
  templateUrl: './quiz-maker.component.html',
  styleUrls: ['./quiz-maker.component.css'],
  providers: [QuizMakerService]
})
export class QuizMakerComponent implements OnInit {

  categories:Category[]=[];
  difficulties:string[]=['Easy','medium','hard'];
  quizDetails:QuizDetails[]=[];
  noQuestions:string='';

  //status:boolean=false
  constructor(private quizService:QuizMakerService){}

  ngOnInit(): void {
    this.getCategories();
  }
  getCategories():void{
    this.quizService.getQuizCategories().subscribe(
        res => {
          const obj = JSON.parse(JSON.stringify(res));
            this.categories =  obj.trivia_categories;
        }
    );
}
createQuiz(inputParam:Quiz){

  this.quizService.createQuiz(inputParam)
      .subscribe(data => {
       let obj=JSON.parse(JSON.stringify(data));
       if(obj.results.length>0){
        this.quizDetails=obj.results;
        this.noQuestions='';
        console.log(this.quizDetails)
        for(let i=0;i<this.quizDetails.length;i++){
this.quizDetails[i].answers=this.quizDetails[i].incorrect_answers;
this.quizDetails[i].answers.splice((this.quizDetails[i].answers.length+1) * Math.random() | 0, 0, this.quizDetails[i].correct_answer)

        }
        console.log(this.quizDetails)
       }
       else{
        debugger;
        this.quizDetails=[];
        this.noQuestions='No questions available for provided category and difficulty level';
       }

      })  ;

}
clickEvent():void{
 // this.status=true;
}
toggle :boolean= false;
// status:string = 'Enable'; 

// enableDisableRule() {
//   debugger;
//  // console.log(event)
//    // = !this.toggle;
//    // this.status = this.toggle ? 'Enable' : 'Disable';
// }

selectedButtonIndex: number=0;

enableDisableRule(index: number) {
  this.selectedButtonIndex = index;
}

isSelected(index: number): boolean {
  return this.selectedButtonIndex === index;
}
}
<div>
    <h2>QUIZ MAKER</h2>
    <div>
      <form #myForm="ngForm" (submit)="createQuiz(myForm.value)" novalidate>

        <select name="categorySelect" id="categorySelect" placeholder="Select a Category" ngModel>
          <option *ngFor="let cat of categories" value="{{cat.id}}">{{cat.name}}</option>

        </select>


          <select name="difficultySelect" id="difficultySelect" ngModel>
            <option *ngFor="let difficultyLevel of difficulties">{{difficultyLevel}}</option>

          </select><button type="submit" id="createBtn" >Create</button>
          </form>
    </div>

    <div *ngIf="quizDetails.length>0">

      <ul>
        <ng-container *ngFor="let obj of quizDetails; let questionIndex = index;">
          <li>
         {{ obj.question }}<br>


          <div class="inner button" *ngFor="let answer of obj.answers; let indexOfelement = index;">
            <button id="{{indexOfelement}}" (click)="enableDisableRule(indexOfelement)" [ngClass]="isSelected(indexOfelement) ? 'green' : 'red'">
              {{answer}}
            </button>
          </div>

          </li>

        </ng-container>
      </ul>

    </div>
    <ng-container>
      <span>{{noQuestions}}</span>
    </ng-container>

</div>
