import { Component ,OnInit} from '@angular/core';
import { QuizMakerService } from '../services/quiz-maker.service';
import { Category } from '../model/category';
@Component({
  selector: 'app-quiz-maker',
  templateUrl: './quiz-maker.component.html',
  styleUrls: ['./quiz-maker.component.css'],
  providers: [QuizMakerService]
})
export class QuizMakerComponent implements OnInit {

  categories:Category[]=[];
  constructor(private quizService:QuizMakerService){}

  ngOnInit(): void {

    this.getCategories();
  }
  getCategories():void{
    this.quizService.getQuizCategories().subscribe(
        res => {
          const obj = JSON.parse(JSON.stringify(res));
            this.categories =  obj.trivia_categories;
            console.log(this.categories);
        }
    );
}
}
<div>
    <h2>QUIZ MAKER</h2>
    <div>
      <ng-container >

        <select name="cars" id="cars">
          <option *ngFor="let cat of categories">{{cat.name}}</option>

        </select>
      </ng-container>

          <select name="cars" id="cars">
            <option value="volvo">Volvo</option>
            <option value="saab">Saab</option>
            <option value="mercedes">Mercedes</option>
            <option value="audi">Audi</option>
          </select><button type="button">Create</button>
    </div>

</div>
